import React from 'react';
import { QueryClient, QueryClientProvider, useMutation, useQuery, useQueryClient } from 'react-query';
import { Link } from 'react-router-dom';
import { toast, ToastContainer } from 'react-toastify';
import translator from '../Translator/translator';
import Contents from './Contents';

function Layout(props) {

    const queryClient = useQueryClient()


    const fetchData = () => {

        console.log('fetchData :>> ');
    }
    const mutation = useMutation(fetchData, {
        onSuccess: () => {
            // Invalidate and refetch
            queryClient.invalidateQueries('todos')
        },
    })


    const { status, data, error, refetch, isLoading } = useQuery("myKey", fetchData, {
        manual: true,
    });



    return (
        <>
            {console.log('status :>> ', status, "data ==>", data)}
            {console.log('isLoading :>> ', isLoading)}
            {/* {console.log('error :>> ', error, "refetch ==>", refetch)} */}
            <header>
                <h2>geomanagement2</h2>
            </header>

            <section>
                <div>

                    {/*  NAV  ******************************************* */}
                    <div>
                        <nav>
                            <ul>
                                <Link to="/countries">
                                    <li>
                                        {translator("countries")}
                                    </li>
                                </Link>

                                <Link to="/countries/new">
                                    <li>
                                        {translator("countriesNew")}
                                    </li>
                                </Link>

                                <Link to="/provinces">
                                    <li>
                                        {translator("provinces")}
                                    </li>
                                </Link>

                                <Link to="/provinces/new">
                                    <li>
                                        {translator("provincesNew")}
                                    </li>
                                </Link>

                                <Link to="/cities">
                                    <li>
                                        {translator("cities ")}
                                    </li>
                                </Link>

                                <Link to="/cities/new">
                                    <li>
                                        {translator("citiesNew ")}
                                    </li>
                                </Link>
                            </ul>
                        </nav>
                    </div>

                    {/*  CONTENT  ******************************************* */}

                    <div className="content">
                        <QueryClientProvider client={queryClient}>
                            <button onClick={() => refetch()}> onClick </button>

                            <Contents />
                        </QueryClientProvider>

                    </div>

                </div>
            </section>



            <footer>
                <p>Footer</p>
            </footer>
        </>
    );
}

export default Layout;
