import { Formik, Form, Field } from 'formik';
import React, { useEffect, useState } from 'react';
import { withRouter } from 'react-router-dom';
import * as Yup from 'yup';
import { geomanagementApi } from '../../service/api';
import CommonField from '../CommonField';

function ProvinceForm(props) {
    const [initialValues, setInitialValues] = useState({})
    const [count, seCount] = useState(true)

    useEffect(async () => {
        if (props.edit) {
            const id = props.location.state && props.location.state.id
            geomanagementApi.get("/province/" + id).then((res) => {
                console.log('res.data.data :>> ', res.data.data);
                setInitialValues(res.data.data)
            })
        }
    }, [])

    const validationSchema = Yup.object().shape({
        enName: Yup.string().required("required"),
        localName: Yup.string().required("required"),
        otherName: Yup.string().required("required"),
        shortCode: Yup.string().required("required"),
    });

    const submit = async (values) => {
        if (props.edit = true) {
            await geomanagementApi.put(`/countries/${values.id}`, values).then((res) => alert(res))
                .catch(err => alert(err))
        } else {
            await geomanagementApi.put(`/countries/${values.id}`, values).then((res) => alert(res))
                .catch(err => alert(err))
        }
    }

    return (
        <div>
            {console.log('props  countries >> ', props)}
            <Formik
                validationSchema={validationSchema}
                initialValues={initialValues}
                onSubmit={submit}
                enableReinitialize
            >
                {({ errors, touched }) => (
                    <Form>

                        <label htmlFor="countryId">countryId</label>
                        <Field
                            name="countryId"
                            render={({ field, form }) =>
                                <Radio.Group
                                    tabIndex="8"
                                    buttonStyle="solid"
                                    name="countryId"
                                    {...field} >
                                    <Radio {...field} value="SHIPPER">{translator("Shipper")} </Radio>
                                    <Radio {...field} value="CONSIGNEE">{translator("Consignee")} </Radio>
                                </Radio.Group>} />
                        {errors.countryId && touched.countryId ? (<div>{errors.countryId}</div>) : null}

                        <CommonField
                            errors={errors}
                            touched={touched}
                        />
                        <button type="submit">Submit</button>
                    </Form>
                )}
            </Formik>
        </div >
    );
}

export default withRouter(ProvinceForm);
