import { Formik, Form, Field } from 'formik';
import React, { useEffect, useState } from 'react';
import { withRouter } from 'react-router-dom';
import * as Yup from 'yup';
import { geomanagementApi } from '../../service/api';
import CommonField from '../CommonField';
import { PrvnSelect } from '../../component2/PrvnComponents';
import { Select } from 'antd';
const { Option } = Select;

function ProvinceForm(props) {
    const [initialValues, setInitialValues] = useState({})
    const [countries, setCountries] = useState(false);
    const [countrieLoading, setCountriesLoading] = useState(false);

    const [count, seCount] = useState(true)

    useEffect(async () => {
        if (props.edit) {
            const id = props.location.state && props.location.state.id
            geomanagementApi.get("/province/" + id).then((res) => {
                console.log('res.data.data :>> ', res.data.data);
                setInitialValues(res.data.data)
            })
        }
    }, [])

    const validationSchema = Yup.object().shape({
        enName: Yup.string().required("required"),
        localName: Yup.string().required("required"),
        otherName: Yup.string().required("required"),
        shortCode: Yup.string().required("required"),
    });

    const submit = async (values) => {
        console.log('values :>> ', values);
        if (props.edit = true) {
            await geomanagementApi.put(`/proninces/${values.id}`, values).then((res) => alert(res))
                .catch(err => alert(err))
        } else {
            await geomanagementApi.put(`/proninces/${values.id}`, values).then((res) => alert(res))
                .catch(err => alert(err))
        }
    }


    const getcountries = async (value) => {
        console.log('value :>> ', value);
        // setCountriesLoading(true)
        // const params = {
        //     term: ""
        // }
        // await geomanagementApi.put(`/countries/`, { params: params }).then((res) => {
        //     let arrayMission = []
        //     res.data.data.elements.map((it) => {
        //         arrayMission.push({
        //             lable: ` نام راننده (${it.firstName}  ${it.lastName})  ,  شماره پلاک (${it.carrierRegister}) `,
        //             data: it,
        //             value: it.id
        //         })
        //     })
        //     // setMissions(arrayMission)

        // }).catch(err => {
        //     console.log('err :>> ', err);
        // })
        //     .finally(() => {
        //         // setMissionLoading(false)
        //     })
    }

    return (
        <div>
            {console.log('props  countries >> ', props)}
            <Formik
                validationSchema={validationSchema}
                initialValues={initialValues}
                onSubmit={submit}
                enableReinitialize
            >
                {({ errors, touched }) => (
                    <Form>

                        <label htmlFor="countryId">countryId555</label>
                        <Field
                            // name="countryId"
                            render={({ field, form }) =>
                                <PrvnSelect

                                    loading={countrieLoading}
                                    allowClear={true}
                                    name={"countryId"}
                                    label={"ماموریت"}
                                    optionvalue={countries}
                                    selectFocus={getcountries}
                                    onSearch={(value) => console.log(value)}

                                />




                            } />

                        {errors.countryId && touched.countryId ? (<div>{errors.countryId}</div>) : null}

                        <CommonField
                            errors={errors}
                            touched={touched}
                        />
                        <button type="submit">Submit</button>
                    </Form>
                )}
            </Formik>
        </div >
    );
}

export default withRouter(ProvinceForm);
