import React, { useEffect, useState } from 'react';
// import Table from '../Teble';

import PrvnTable from '../../component2/PrvnTable';
import Table from '../Teble';
import { EditOutlined } from '@ant-design/icons';
import { Link } from 'react-router-dom';
import translator from '../../Translator/translator';
import DeleteModal from '../../component2/DeleteModal';
import { geomanagementApi } from '../../service/api';
import { PrvnInput } from '../../component2/PrvnComponents';
import { Pagination } from 'antd';
import SearchForm from './SearchForm';

function Countries(props) {
    const [dataSource, setDataSource] = useState([])
    const [name, setName] = useState("")

    // useEffect(() => {
    //     const params = { name: name }

    // }, [name])

    const columns = [{
        title: 'nameLocal',
        dataIndex: 'localName',
        key: '1',
    },
    {
        title: 'otherName',
        dataIndex: 'otherName',
        key: '2',
    },
    {
        title: 'enName',
        dataIndex: 'enName',
        key: '3',
    },
    {
        title: 'createTime',
        dataIndex: 'createTime',
        key: '4',
    },
    {
        title: translator('Action'),
        key: 'action',
        render: (text, record) => (
            <Link
                to={{
                    pathname: `/countries/${record.id}/edit`,
                    state: { id: record.id }
                }}
            >
                <EditOutlined />
            </Link>
        ),
    },
    {
        title: 'deleted',
        dataIndex: 'deleted',
        key: 'deleted',
        render: (text, record) => {
            return <>
                <DeleteModal id={record.id} record={record} type={"countries"} />
            </>
        }
    },
    ]

    const getData = async () => {
        const params = { name: name }

        try {
            const res = await geomanagementApi.get("/countries", { params: params })
            setDataSource(res.data.data)

        } catch (error) {
            setDataSource([])

            console.log('getData :>> ', error);
        }

    }
    // const mySubmitHandler = () => {
    //     console.log('name :>> ', name);
    //     if (name == undefined || name == "") {
    //         alert("بخشی از نام را وارد کنید")
    //     } else {
    //         getData()

    //     }
    // }
    const mySubmitHandler = (event) => {
        event.preventDefault()
        console.log('name :>> ', name);
        getData()

    }

    const reset = (event) => {
        event.preventDefault();
        setName("")
        setDataSource([])

    }


    return (
        <div>
            <h2>{translator("countries")}
                <Link to="/countries/new"><button> {translator("create new countries")}</button></Link></h2>
            <SearchForm
                value={name}
                mySubmitHandler={mySubmitHandler}
                reset={reset}
            ></SearchForm>

            <div>
                <h3>{translator("search")}</h3>
                <form>
                    <span> name</span>
                    <input required type="text" value={name} onChange={(e) => {
                        setName(e.target.value)
                    }} />
                    <button onClick={mySubmitHandler} > {translator("search")}</button>
                    <button onClick={reset} >{translator("reset")}</button>
                </form>
            </div>

            <div >
                <PrvnTable
                    dataSource={dataSource}
                    columns={columns}
                    pagination={false}
                />
                <Pagination
                    size="small"
                    total={50}
                    showTotal={(total) => `Total ${total} items`}
                    showSizeChanger
                // showQuickJumper
                />
            </div>
        </div>
    );
}

export default Countries;
