import React, { useEffect, useState } from 'react';
import { Formik, Form, Field } from 'formik';
import { withRouter } from 'react-router-dom';
import * as Yup from 'yup';
import { geomanagementApi } from '../../service/api';
import CommonField from '../CommonField';
import { PrvnSelect } from '../../component2/PrvnComponents';
import { Select } from 'antd';
import { toast } from 'react-toastify';
const { Option } = Select;


function CitiesForm(props) {
    const [initialValues, setInitialValues] = useState({})
    const [countries, setCountries] = useState([]);
    // const [data, setCountries] = useState(false);
    const [countrieLoading, setCountriesLoading] = useState(false);

    const [count, seCount] = useState(true)

    useEffect(async () => {
        if (props.edit) {
            const id = props.location.state && props.location.state.id
            geomanagementApi.get("/cities/" + id)
                .then((res) => {
                    console.log('res.data.data :>> ', res.data.data);
                    setInitialValues(res.data.data)
                }).catch((err) => {
                    toast.error(translator("errorDone"));
                    console.log('err :>> ', err);
                })
        }
    }, [])

    const validationSchema = Yup.object().shape({
        enName: Yup.string().required("required"),
        localName: Yup.string().required("required"),
        otherName: Yup.string().required("required"),
        shortCode: Yup.string().required("required"),
    });

    const submit = async (values) => {
        console.log('values :>> ', values);
        if (props.edit == true) {
            await geomanagementApi.put(`/cities/${values.id}`, values)
                .then(() => {
                    toast.success(translator("successDone"));
                })
                .catch((err) => {
                    toast.error(translator("errorDone"));
                    console.log('err :>> ', err);
                })
        } else {
            await geomanagementApi.post(`/cities`, values).then((res) => {
                toast.success(translator("successDone"));
                // props.history.push("/cities")
            }).catch(err => {
                toast.error(translator("errorDone"));
                console.log('err :>> ', err)
            })
        }
    }


    const getcountries = async (value) => {
        console.log('value :>> ', value);
        const params = {
            name: value
        }
        geomanagementApi.get(`/provinces`, { params: params })
            .then(() => {
                if (res.data.data) {
                    setCountries(res.data.data)
                }
            }).catch((err) => {
                toast.error(translator("errorDone"));
                console.log('err :>> ', err);
            })
    }
    const options = countries.map(d => <Option key={d.id}>{d.localName}</Option>)

    return (
        <div>
            <Formik
                validationSchema={validationSchema}
                initialValues={initialValues}
                onSubmit={submit}
                enableReinitialize
            >
                {({ errors, touched, setFieldValue }) => (
                    <Form>

                        <label htmlFor="provinceId">provinceId</label>
                        <Field
                            name="provinceId"
                            render={({ field, form }) =>
                                <Select
                                    {...field}
                                    showSearch
                                    value={form.values["provinceId"]}
                                    style={{ width: "300px" }}
                                    defaultActiveFirstOption={false}
                                    showArrow={false}
                                    filterOption={false}
                                    onSearch={getcountries}
                                    onChange={(value, option) =>
                                        form.setFieldValue("provinceId", value)
                                    }
                                    notFoundContent={null}
                                >
                                    {options}
                                </Select>

                            } />

                        {errors.provinceId && touched.provinceId ? (<div>{errors.provinceId}</div>) : null}

                        <CommonField
                            errors={errors}
                            touched={touched}
                        />
                        <button type="submit">Submit</button>
                    </Form>
                )}
            </Formik>
        </div >
    );
}
export default withRouter(CitiesForm);
