import React, { useEffect, useState } from 'react';
// import Table from '../Teble';

import PrvnTable from '../PrvnTable';
import Table from '../Teble';
import { EditOutlined } from '@ant-design/icons';
import { Link } from 'react-router-dom';
import translator from '../../Translator/translator';
import DeleteModal from '../DeleteModal';
import { geomanagementApi } from '../../service/api';
import { PrvnInput } from '../PrvnComponents';
import { Pagination } from 'antd';
import SearchForm from './SearchForm';

function Countries(props) {
    const [dataSource, setDataSource] = useState([])
    const [name, setName] = useState("")
    const [visibleMessage, setVisibleMessage] = useState(false)
    const [contentMessage, setContentMessage] = useState(undefined)

    // useEffect(() => {
    //     const params = { name: name }

    // }, [name])

    const columns = [
        {
            title: translator('shortCode'),
            dataIndex: 'shortCode',
            key: '10'
        },

        {
            title: translator('otherName'),
            dataIndex: 'otherName',
            key: '2',
        },
        {
            title: translator('enName'),
            dataIndex: 'enName',
            key: '3',
        },
        {
            title: translator('localName'),
            dataIndex: 'localName',
            key: '1',
        },
        {
            title: translator('createTime'),
            dataIndex: 'createTime',
            key: '4',
        }, {
            title: translator('updateTime'),
            dataIndex: 'updateTime',
            key: '5',
        },
        {
            title: translator('Action'),
            key: 'action',
            render: (text, record) => (
                <Link
                    to={{
                        pathname: `/countries/${record.id}/edit`,
                        state: { id: record.id }
                    }}
                >
                    <EditOutlined />
                </Link>
            ),
        },
        {
            title: 'deleted',
            dataIndex: 'deleted',
            key: 'deleted',
            render: (text, record) => {
                return <>
                    <DeleteModal getData={getData} id={record.id} record={record} type={"countries"} />
                </>
            }
        },
    ]

    const getData = async () => {
        const params = { name: name }
        try {
            const res = await geomanagementApi.get("/countries", { params: params })
            setDataSource(res.data.data)
        } catch (error) {
            setDataSource([])
            console.log('getData :>> ', error);
        }
    }

    const mySubmitHandler = (event) => {
        event.preventDefault()
        console.log('name :>> ', name);
        getData()

    }

    const reset = (event) => {
        event.preventDefault();
        setName("")
        setDataSource([])

    }


    return (
        <div>
            <h2>{translator("countries")}
                <Link to="/countries/new"><button className={"btn"}>{translator("create new countries")}</button></Link></h2>
            <SearchForm
                onChange={(e) => {
                    setName(e.target.value)
                }} value={name}
                mySubmitHandler={mySubmitHandler}
                reset={reset}
            />
            <div >
                <PrvnTable
                    dataSource={dataSource}
                    columns={columns}
                    pagination={false}
                />
                <Pagination
                    size="small"
                    total={50}
                    showTotal={(total) => `Total ${total} items`}
                    showSizeChanger
                />
            </div>
        </div>
    );
}

export default Countries;
