import { Formik, Form, Field } from 'formik';
import React, { useEffect, useState } from 'react';
import { withRouter } from 'react-router-dom';
import { toast } from 'react-toastify';
import * as Yup from 'yup';
import { PrvnButton, PrvnForm, PrvnFormItem, PrvnFormItemInput, PrvnUseForm } from '../../component2/PrvnComponents';
import { geomanagementApi } from '../../service/api';
import translator from '../../Translator/translator';
import CommonField from '../CommonField';
import { QueryClient, QueryClientProvider, useMutation, useQuery, useQueryClient } from 'react-query';
// import { message } from 'antd';
import { Message, message } from '../Message';

function CountriesForm(props) {
    const [form] = PrvnUseForm()
    const [initialValues, setInitialValues] = useState({})
    const [count, seCount] = useState(true)
    const [visibleMessage, setVisibleMessage] = useState(true)

    useEffect(async () => {
        if (props.edit) {
            onFill()
        }
    }, [])
    useEffect(async () => {
    }, [visibleMessage])

    const id = props.location.state && props.location.state.id


    const onFinish = async (values) => {
        console.log('values :>> ', values);
        console.log('init :>> ', initialValues);
        if (props.edit == true) {
            await geomanagementApi.put(`/countries/${id}`, values).then((res) => {
                // props.history.push("/countries")
                alert(res.data)
            }).catch(err => alert(err))
        } else {
            await geomanagementApi.post(`/countries`, values).then((res) => {
                // props.history.push("/countries")
                alert(res)
            }).catch(err => alert(err))
        }
    }

    const onFill = () => {
        console.log('props.initialValues :>> ', props.initialValues);
        // const id = props.location.state && props.location.state.id
        geomanagementApi.get("/countries/" + id).then((response) => {
            form.setFieldsValue(response.data.data)
        })

    };
    const onReset = () => {
        form.resetFields();
    };

    const validationSchema = Yup.object().shape({
        enName: Yup.string().required("required"),
        localName: Yup.string().required("required"),
        otherName: Yup.string().required("required"),
        shortCode: Yup.string().required("required"),
    });

    const queryClient = useQueryClient()



    // const mutation = useMutation(onFinish, {
    //     onSuccess: () => {
    //         // Invalidate and refetch
    //         queryClient.invalidateQueries('todos')
    //     },
    // })


    // const { status, data, error, refetch } = useQuery("myKey", onFinish);




    return (
        <>
            <button onClick={() => setVisibleMessage(true)} > onclick</button>
            <button onClick={() => setVisibleMessage(false)} > false</button>
            {visibleMessage &&
                <Message visible={true} message="message" dealay="5000"></Message>
            }
            {/* <Message visible={true} message="message" dealay="1000"></Message> */}


            <PrvnForm
                form={form}
                name="control-hooks"
                // initialValues={initialValues}
                // validateMessages={validateMessages}
                onFinish={onFinish}>



                <PrvnFormItemInput
                    rules={[{ required: true, message: `${translator("shortCode")} الزامی است` }, { min: 2, message: 'طول عبارت کوتاه است' }]}
                    name="shortCode"
                    label={translator("shortCode")}
                />
                <PrvnFormItemInput
                    rules={[{ required: true, message: `${translator("otherName")} الزامی است` }, { min: 2, message: 'طول عبارت کوتاه است' }]}
                    name="otherName"
                    label={translator("otherName")}
                />
                <PrvnFormItemInput
                    rules={[{ required: true, message: `${translator("otherName")} الزامی است` }, { min: 2, message: 'طول عبارت کوتاه است' }]}
                    name="enName"
                    label={translator("enName")}
                />
                <PrvnFormItemInput
                    rules={[{ min: 2, message: 'طول عبارت کوتاه است' }]}
                    name="localName"
                    label={translator("localName")}
                />

                <PrvnFormItem >

                    <PrvnButton type="primary" htmlType="submit">
                        Submit
                 </PrvnButton>

                    <PrvnButton htmlType="Prvnbutton" onClick={onReset}>
                        Reset
            </PrvnButton>

                </PrvnFormItem>
            </PrvnForm >
        </>

    );
}

export default withRouter(CountriesForm);
