import { Formik, Form, Field } from 'formik';
import React, { useEffect, useState } from 'react';
import { withRouter } from 'react-router-dom';
import * as Yup from 'yup';
import { PrvnForm, PrvnUseForm } from '../../component2/PrvnComponents';
import { geomanagementApi } from '../../service/api';
import CommonField from '../CommonField';

function CountriesForm(props) {
    const [form] = PrvnUseForm()
    const [initialValues, setInitialValues] = useState({})
    const [count, seCount] = useState(true)

    useEffect(async () => {
        if (props.edit) {
            const id = props.location.state && props.location.state.id
            geomanagementApi.get("/countries/" + id).then((response) => {
                const res = response.data.data
                // const preData = {
                //     "enName": res.enName,
                //     localName: res.data,
                //     otherName: res.data,
                //     shortCode: res.data,
                // }
                // setInitialValues(preData)
                setInitialValues(response.data.data)
            })
        }
    }, [])

    const validationSchema = Yup.object().shape({
        enName: Yup.string().required("required"),
        localName: Yup.string().required("required"),
        otherName: Yup.string().required("required"),
        shortCode: Yup.string().required("required"),
    });

    const onFinish = async (values) => {
        if (props.edit == true) {
            await geomanagementApi.put(`/countries/${values.id}`, values).then((res) => {
                props.history.push("/countries")
                alert(res.data)
            }).catch(err => alert(err))
        } else {
            await geomanagementApi.post(`/countries`, values).then((res) => {
                props.history.push("/countries")
                alert(res)
            }).catch(err => alert(err))
        }
    }

    const onFill = () => {
        console.log('props.initialValues :>> ', props.initialValues);
        if (props.edit) {
            const id = props.location.state && props.location.state.id
            geomanagementApi.get("/countries/" + id).then((response) => {
                const res = response.data.data
                // const preData = {
                //     "enName": res.enName,
                //     localName: res.data,
                //     otherName: res.data,
                //     shortCode: res.data,
                // }
                // setInitialValues(preData)
                form.setFieldsValue(response.data.data)
            })
        }
        // form.setFieldsValue(props.initialValues)
    };

    return (
        <PrvnForm
            form={form}
            name="control-hooks"
            // initialValues={initialValues}
            onFinish={onFinish}>
                <PrvnFormItemInput
                    rules={[{ required: true }]}
                    name="enName"
                    label={translator("enName")}
                />
                <PrvnFormItem {...tailLayout}>

                    <PrvnButton type="primary" htmlType="submit">
                        Submit
                 </PrvnButton>

                    <PrvnButton htmlType="Prvnbutton" onClick={onReset}>
                        Reset
            </PrvnButton>

                </PrvnFormItem>
        </PrvnForm >

        // <div>
        //     <Formik
        //         validationSchema={validationSchema}
        //         // initialValues={initialValues}
        //         initialValues={{
        //             enName: "United Arab Emirates",
        //             localName: "امارات متحده عربی",
        //             otherName: "othername",
        //             shortCode: "AE",
        //         }}
        //         onSubmit={submit}
        //     // enableReinitialize
        //     >
        //         {({ errors, touched,values }) => (
        //             <Form>
        //                 <label htmlFor="enName">enName</label>
        //                 <Field name="enName" />
        //                 {errors.enName && touched.enName ? (<div>{errors.enName}</div>) : null}

        //                 <label htmlFor="localName">localName</label>
        //                 <Field id="localName" name="localName" />
        //                 {errors.localName && touched.localName ? (<div>{errors.localName}</div>) : null}

        //                 <label htmlFor="otherName">otherName</label>
        //                 <Field id="otherName" name="otherName" />
        //                 {errors.otherName && touched.otherName ? (<div>{errors.otherName}</div>) : null}

        //                 <label htmlFor="shortCode">shortCode</label>
        //                 <Field id="shortCode" name="shortCode" />
        //                 {errors.shortCode && touched.shortCode ? (<div>{errors.shortCode}</div>) : null}

        //                 {/* <CommonField
        //                     errors={errors}
        //                     touched={touched}
        //                 /> */}
        //                 <button type="submit">Submit</button>
        //             </Form>
        //         )}
        //     </Formik>
        // </div >
    );
}

export default withRouter(CountriesForm);
