import React from 'react';
import { useQuery } from 'react-query';
import { Link } from 'react-router-dom';
import { toast, ToastContainer } from 'react-toastify';
import translator from '../Translator/translator';
import Contents from './Contents';

function Layout(props) {

    const notify = () => toast.success('ðŸ¦„ Wow so easy!', {
        position: "top-right",
        autoClose: 5000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: 0,
    });
    const fetchData = () => {
        console.log('fetchData :>> ');
    }

    const { status, data, error, refetch } = useQuery("myKey", fetchData);


    return (
        <>
            <header>
                <h2>geomanagement2</h2>
            </header>

            <section>
                <div>




                    {/*  NAV  ******************************************* */}
                    <div>
                        <nav>
                            <ul>
                                <Link to="/countries">
                                    <li>
                                        {translator("countries")}
                                    </li>
                                </Link>

                                <Link to="/countries/new">
                                    <li>
                                        {translator("countriesNew")}
                                    </li>
                                </Link>

                                <Link to="/provinces">
                                    <li>
                                        {translator("provinces")}
                                    </li>
                                </Link>

                                <Link to="/provinces/new">
                                    <li>
                                        {translator("provincesNew")}
                                    </li>
                                </Link>

                                <Link to="/cities">
                                    <li>
                                        {translator("cities ")}
                                    </li>
                                </Link>

                                <Link to="/cities/new">
                                    <li>
                                        {translator("citiesNew ")}
                                    </li>
                                </Link>
                            </ul>
                        </nav>
                    </div>

                    {/*  CONTENT  ******************************************* */}

                    <div className="content">
                        <button onClick={() => refetch()}> onClick </button>

                        <Contents />
                    </div>

                </div>
            </section>



            <footer>
                <p>Footer</p>
            </footer>
        </>
    );
}

export default Layout;
