import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import DeleteModal from '../../component2/DeleteModal';
import { geomanagementApi } from '../../service/api';
import translator from '../../Translator/translator';
import { EditOutlined } from '@ant-design/icons';
import PrvnTable from '../../component2/PrvnTable';

function Province(props) {
    const [dataSource, setDataSource] = useState([])
    const [name, setName] = useState(undefined)

    useEffect(() => {
        const params = { name: name }
        if (name !== undefined) {
            geomanagementApi.get("/provinces", { params: params }).then((res) => {
                setDataSource(res.data.data)
            })
        }

    }, [name])

    const getData = () => {
        const params = { name: name }

        geomanagementApi.get("/provinces", { params: params }).then((res) => {
            setDataSource(res.data.data)
        })
    }

    const columns = [{
        title: 'nameLocal',
        dataIndex: 'localName',
        key: '1',
    },
    {
        title: 'otherName',
        dataIndex: 'otherName',
        key: '2',
    },
    {
        title: 'enName',
        dataIndex: 'enName',
        key: '3',
    },
    {
        title: 'createTime',
        dataIndex: 'createTime',
        key: '4',
    },
    {
        title: translator('Action'),
        key: 'action',
        render: (text, record) => (
            <Link
                to={{
                    pathname: `/provinces/${record.id}/edit`,
                    state: { id: record.id }
                }}
            >
                <EditOutlined />
            </Link>
        ),
    },
    {
        title: 'deleted',
        dataIndex: 'deleted',
        key: 'deleted',
        render: (text, record) => {
            return <>
                <DeleteModal id={record.id} record={record} type={"provinces"} />

            </>
        }
    },
    ]
    const mySubmitHandler = (event) => {
        event.preventDefault()
        console.log('name :>> ', name);
            getData()

    }

    const reset = (event) => {
        event.preventDefault();
        setName("")
        setDataSource([])

    }


    return (
        <div>
            <div>
                <h3>{translator("search")}</h3>
                <form>
                    <span> name</span>
                    <input required type="text" value={name} onChange={(e) => {
                        setName(e.target.value)
                    }} />
                    <button onClick={mySubmitHandler} > {translator("search")}</button>
                    <button onClick={reset} >{translator("reset")}</button>
                </form>
            </div>

            <PrvnTable
                dataSource={dataSource}
                columns={columns}
                pagination={false}
            >
            </PrvnTable>
        </div>
    );
}
export default Province;
