import { getDefaultNormalizer } from '@testing-library/react';
import { Formik, Field, Form } from 'formik';
import React, { useEffect, useState } from 'react';
import * as Yup from 'yup';
import { geomanagementApi } from '../service/api';
import translator from '../Translator/translator';

function WrapperForm(props) {
    const [initialValues, setInitialValues] = useState({})
    const [count, seCount] = useState(true)

    console.log('props :>> ', props);

    useEffect(async () => {
        if (props.edit) {
            geomanagementApi.get("/countries/3").then((res) => {
                console.log('res.data.data :>> ', res.data.data);
                setInitialValues(res.data.data)
            })
        }
        console.log('initialValues :>> ', initialValues);
    }, [])

    const validationSchema = Yup.object().shape({
        enName: Yup.string().required("required").min(2, 'Too Short!'),
    });

    const submitHandler = (values, actions) => {
        console.log('values :>> ', values);
        console.log('actions :>> ', actions);
    }

    const submit = async (values) => {
        await geomanagementApi.put(`/countries/${values.id}`, values)
            .then((res) => alert(res))
            .catch(err => alert(err))
    }

    return (
        <div>
            <Formik
                validationSchema={validationSchema}
                initialValues={initialValues}
                onSubmit={submit}
                enableReinitialize
            >
                {({ errors, touched }) => (
                    console.log('errors >> ', errors),
                    console.log('touched :>> ', touched),
                    <Form>
                        <label htmlFor="enName">enName <span> *</span></label>
                        <Field

                            id="enName"
                            name="enName" />
                        {errors.name && touched.enName ? (<div>{errors.enName}</div>) : null}

                        <label htmlFor="localName">localName  <span> *</span></label>
                        <Field
                            required
                            id="localName"
                            name="localName" />
                        <label htmlFor="otherName">otherName  <span> *</span></label>
                        <Field
                            required
                            id="otherName"
                            name="otherName" />
                        <label htmlFor="shortCode">shortCode  <span> *</span></label>
                        <Field
                            required
                            id="shortCode"
                            name="shortCode" />
                        <button type="submit">Submit</button>
                    </Form>
                )}
            </Formik>
        </div >
    );
}

export default WrapperForm;
