import Modal from 'antd/lib/modal/Modal';
import React, { useEffect, useState } from 'react';
import translator from '../Translator/translator';
import { PrvnCol, PrvnTag } from './PrvnComponents';
import { DeleteTwoTone } from '@ant-design/icons';
import { geomanagementApi } from '../service/api';
import { toast } from 'react-toastify';


function DeleteModal(props) {
    console.log('deleteModal :>> ', props);
    const [isModalVisible, setIsModalVisible] = useState(false);
    // const [isModalVisible, setIsModalVisible] = useState(false);search param
    useEffect(() => { }, [isModalVisible])
    const showModal = () => {
        setIsModalVisible(true);
    };

    const handleOk = (e) => {
        const { id } = props.id
        setIsModalVisible(false);
        geomanagementApi.delete(`/${props.type}/${props.id}`)
            .then(() => {
                toast.success(translator("successDone"));
                props.getData();
            })
            .catch(() => {
                toast.error(translator("errorDone"));
            })
    };

    const handleCancel = () => {
        setIsModalVisible(false);
    };
    return (
        <div classname="modal">
            {/* <PrvnTag color="red"
                className="prvn-tag-hover"
                onClick={showModal} >
                <DeleteTwoTone   className="prvn-tag-hover" onClick={showModal} />
            </PrvnTag> */}
            <span onClick={showModal} >
                {props.btn}
            </span>

            <DeleteTwoTone className="prvn-tag-hover" onClick={showModal} />

            {isModalVisible &&
                <div className="modal">
                    <p > از حذف این رکورد اطمینان دارید</p>
                    <button onClick={() => { handleOk() }} >ok</button>
                    <button onClick={handleCancel}>cancel</button>
                </div>}
        </div>
    )
}

export default DeleteModal;
