import { getDefaultNormalizer } from '@testing-library/react';
import { Formik, Field, Form } from 'formik';
import React, { useEffect, useState } from 'react';
import * as Yup from 'yup';
import { geomanagementApi } from '../service/api';
import translator from '../Translator/translator';
class WrapperForm extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            initialValues: { enName: "test" }
        }
    }


    componentDidMount() {
        geomanagementApi.get("/countries/3").then((res) => {
            this.setState({ initialValues: res.data.data })
        })
    }

    validationSchema = Yup.object().shape({
        personUuid: Yup.string().required(),

    });
    submitHandler = (values, actions) => {
        console.log('values :>> ', values);
        console.log('actions :>> ', actions);
    }

    // helpValue = (isTouched, hasErr) => {
    //     return isTouched && hasErr
    // }
    // validateStatus = (isTouched, hasErr) => {
    //     return isTouched && hasErr ? 'error' : ''
    // }
    submit = async (values) => {
        // await new Promise((r) => setTimeout(r, 500));
        await geomanagementApi.post("/countries", values).then((res) =>
            alert(res)).catch(err => alert(err))
        // alert(JSON.stringify(values, r, 2));
    }

    // const getinitialData = async () => {
    //     const res = await geomanagementApi.get("/countries/3")
    //     let data = res.data && res.data.data
    //     return res.data.data

    // }
    render() {
        const { initialValues } = this.state
        console.log('this.state.initialValues :>> ', this.state.initialValues);
        return (
            <div>
                <Formik
                    initialValues={this.state.initialValues}
                    onSubmit={this.submit}
                    enableReinitialize
                    render={
                        ({
                            values,
                            errors,
                            touched,
                            status,
                            dirty,
                            handleChange,
                            handleBlur,
                            handleSubmit,
                            isSubmitting,
                            isValid,
                            handleReset,
                            setTouched
                        }) => (
                                <Form>
                                    <label htmlFor="enName">enName</label>
                                    <Field id="enName" name="enName" />

                                    <label htmlFor="localName">localName</label>
                                    <Field id="localName" name="localName" />

                                    <label htmlFor="otherName">otherName</label>
                                    <Field
                                        id="otherName"
                                        name="otherName"
                                    />
                                    <label htmlFor="shortCode">shortCode</label>
                                    <Field
                                        id="shortCode"
                                        name="shortCode"
                                    />
                                    <button type="submit">Submit</button>
                                </Form>
                            )}>
                </Formik>


            </div>
        );
    }
}


export default WrapperForm;
