import { Formik, Form } from 'formik';
import React, { useEffect, useState } from 'react';
import { withRouter } from 'react-router-dom';
import * as Yup from 'yup';
import { geomanagementApi } from '../../service/api';
import CommonField from '../CommonField';

function CountriesForm(props) {
    const [initialValues, setInitialValues] = useState({})
    const [count, seCount] = useState(true)

    useEffect(async () => {
        if (props.edit) {
            const id = props.location.state && props.location.state.id
            geomanagementApi.get("/countries/" + id).then((res) => {
                setInitialValues(res.data.data)
            })
        }
    }, [])

    const validationSchema = Yup.object().shape({
        enName: Yup.string().required("required"),
        localName: Yup.string().required("required"),
        otherName: Yup.string().required("required"),
        shortCode: Yup.string().required("required"),
    });

    const submit = async (values) => {
        console.log('values :>> ', values);
        if (props.edit == true) {
            await geomanagementApi.put(`/countries/${values.id}`, values).then((res) => {
                props.history.push("/countries")
                alert(res.data)
            }).catch(err => alert(err))
        } else {
            await geomanagementApi.post(`/countries`, values).then((res) => {
                props.history.push("/countries")
                alert(res)
            }).catch(err => alert(err))
        }
    }

    return (
        <div>
            <Formik
                validationSchema={validationSchema}
                initialValues={initialValues}
                onSubmit={submit}
                enableReinitialize
            >
                {({ errors, touched }) => (
                    <Form>
                        <CommonField
                            errors={errors}
                            touched={touched}
                        />
                        <button type="submit">Submit</button>
                    </Form>
                )}
            </Formik>
        </div >
    );
}

export default withRouter(CountriesForm);
