import { Formik, Form, Field } from 'formik';
import React, { useEffect, useState } from 'react';
import { withRouter } from 'react-router-dom';
import * as Yup from 'yup';
import { PrvnButton, PrvnForm, PrvnFormItem, PrvnFormItemInput, PrvnFormItemSelect, PrvnUseForm } from '../../component2/PrvnComponents';
import { geomanagementApi } from '../../service/api';
import translator from '../../Translator/translator';
import CommonField from '../CommonField';

function CountriesForm(props) {
    const [form] = PrvnUseForm()
    const [initialValues, setInitialValues] = useState({})
    const [count, seCount] = useState(true)
    const [countries, setCountries] = useState([]);


    useEffect(async () => {
        if (props.edit) {
            onFill()
        }
    }, [])

    const validationSchema = Yup.object().shape({
        enName: Yup.string().required("required"),
        localName: Yup.string().required("required"),
        otherName: Yup.string().required("required"),
        shortCode: Yup.string().required("required"),
    });

    const onFinish = async (values) => {
        if (props.edit == true) {
            await geomanagementApi.put(`/countries/${values.id}`, values).then((res) => {
                props.history.push("/countries")
                alert(res.data)
            }).catch(err => alert(err))
        } else {
            await geomanagementApi.post(`/countries`, values).then((res) => {
                props.history.push("/countries")
                alert(res)
            }).catch(err => alert(err))
        }
    }

    const onFill = () => {
        console.log('props.initialValues :>> ', props.initialValues);
        const id = props.location.state && props.location.state.id
        geomanagementApi.get("/countries/" + id).then((response) => {

            form.setFieldsValue(response.data.data)
        })

    };
    const onReset = () => {
        form.resetFields();
    };

    const getcountries = async (value) => {
        console.log('value :>> ', value);
        const params = {
            name: value
        }
        geomanagementApi.get(`/provinces`, { params: params }).then(res => {

            res.data.data.map((it) => {
                arrayMission.push({
                    lable: ` نام راننده (${it.firstName}  ${it.lastName})  ,  شماره پلاک (${it.carrierRegister}) `,
                    data: it,
                    value: it.id
                })
            })
            // if (res.data.data) {
            //     setCountries(res.data.data)
            // }
            setCountries(res.data.data)
        })

    }
    return (

        // <Select
        //                                     {...field}
        //                                     showSearch
        //                                     value={form.values["countryId"]}
        //                                     style={{ width: "300px" }}
        //                                     defaultActiveFirstOption={false}
        //                                     showArrow={false}
        //                                     filterOption={false}
        //                                     onSearch={getcountries}
        //                                     onChange={(value, option) =>
        //                                         form.setFieldValue("countryId", value)
        //                                     }
        //                                     notFoundContent={null}
        //                                 >
        //                                     {options}
        //                                 </Select>


        <PrvnForm
            form={form}
            name="control-hooks"
            // initialValues={initialValues}
            onFinish={onFinish}>

            <PrvnFormItemSelect
                showSearch

                allowClear={true}
                name={"countryId"}
                label={translator("countryId")}
                optionvalue={countries}
                // selectFocus={getMissions}
                onSearch={getcountries}
                // onPopupScroll={(e) => console.log("e", e)}
                onSelect={(option, LabeledValue) => {
                    console.log('LabeledValue :>> ', LabeledValue);
                    let selected
                    if (LabeledValue !== undefined) {
                        selected = JSON.parse(LabeledValue && LabeledValue.key)
                        console.log('selected :>> ', selected.data);
                        let additional = {
                            driverInfo: `${selected.data.firstName} ${selected.data.lastName} `,
                            vehicleInfo: `${selected.data.carrierCode} , پلاک :(${selected.data.carrierRegister})`
                        }
                    }
                }}
            />
            <PrvnFormItemInput
                rules={[{ required: true }]}
                name="enName"
                label={translator("enName")}
            />
            <PrvnFormItemInput
                rules={[{ required: true }]}
                name="localName"
                label={translator("localName")}
            />
            <PrvnFormItemInput
                rules={[{ required: true }]}
                name="otherName"
                label={translator("otherName")}
            />
            <PrvnFormItemInput
                rules={[{ required: true }]}
                name="shortCode"
                label={translator("shortCode")}
            />
            <PrvnFormItem >

                <PrvnButton type="primary" htmlType="submit">
                    Submit
                 </PrvnButton>

                <PrvnButton htmlType="Prvnbutton" onClick={onReset}>
                    Reset
            </PrvnButton>

            </PrvnFormItem>
        </PrvnForm >

    );
}

export default withRouter(CountriesForm);
