import React, { useEffect, useState } from 'react';
// import Table from '../Teble';

import PrvnTable from '../../component2/PrvnTable';
import Table from '../Teble';
import { EditOutlined } from '@ant-design/icons';
import { Link } from 'react-router-dom';
import translator from '../../Translator/translator';
import DeleteModal from '../../component2/DeleteModal';
import { geomanagementApi } from '../../service/api';
import { PrvnInput } from '../../component2/PrvnComponents';

function Cities(props) {
    const [dataSource, setDataSource] = useState([])
    const [name, setName] = useState(undefined)

    useEffect(() => {
        const params = { term: name }
        if (name !== undefined) {
            geomanagementApi.get("/cities", { params: params }).then((res) => {
                setDataSource(res.data.data)
            })
            .catch((err) => {
                toast.error(translator("errorDone"));
                console.log('err :>> ', err);
            })

        }

    }, [name])

    const columns = [{
        title: 'nameLocal',
        dataIndex: 'localName',
        key: '1',
    },
    {
        title: 'otherName',
        dataIndex: 'otherName',
        key: '2',
    },
    {
        title: 'enName',
        dataIndex: 'enName',
        key: '3',
    },
    {
        title: 'createTime',
        dataIndex: 'createTime',
        key: '4',
    },
    {
        title: translator('Action'),
        key: 'action',
        render: (text, record) => (
            <Link
                to={{
                    pathname: `/cities/${record.id}/edit`,
                    state: { id: record.id }
                }}
            >
                <EditOutlined />
            </Link>
        ),
    },
    {
        title: 'deleted',
        dataIndex: 'deleted',
        key: 'deleted',
        render: (text, record) => {
            return <>
                <DeleteModal id={record.id} record={record} type={"cities"} />
            </>
        }
    },
    ]


    return (
        <div>
            <span> name</span>

            <input type="text" value={name} onChange={(e) => {
                console.log('e.target :>> ', e.target);
                if (e.target.value == "") {
                    setName(undefined)
                } else {
                    setName(e.target.value)

                }
            }} />
            {name == undefined ? <> برای مشاهده لیست باید بخشی از نام را وارد کنید</> : ""}
            <PrvnTable
                dataSource={dataSource}
                columns={columns}
                pagination={false}
            >
            </PrvnTable>
        </div>
    );
}

export default Cities;
