import { Formik, Field, Form } from 'formik';
import React from 'react';
import * as Yup from 'yup';
import translator from '../Translator/translator';

function WrapperForm(props) {
    const initialValues = {}
    const validationSchema = Yup.object().shape({
        personUuid: Yup.string().required(),

    });
    const submitHandler = (values, actions) => {
        console.log('values :>> ', values);
        console.log('actions :>> ', actions);
    }

    // helpValue = (isTouched, hasErr) => {
    //     return isTouched && hasErr
    // }
    // validateStatus = (isTouched, hasErr) => {
    //     return isTouched && hasErr ? 'error' : ''
    // }
    const submit = async (values) => {
        await new Promise((r) => setTimeout(r, 500));
        alert(JSON.stringify(values, null, 2));
    }

    return (
        <div>
            <Formik
                initialValues={{
                    firstName: '',
                    lastName: '',
                    email: '',
                }}
                onSubmit={async (values) => {
                    await new Promise((r) => setTimeout(r, 500));
                    alert(JSON.stringify(values, null, 2));
                }}
            >
                <Form>
                    <label htmlFor="enName">First Name</label>
                    <Field id="enName" name="firstName" placeholder="Jane" />

                    <label htmlFor="localName">localName</label>
                    <Field id="localName" name="localName" placeholder="Doe" />

                    <label htmlFor="otherName">otherName</label>
                    <Field
                        id="otherName"
                        name="otherName"
                        placeholder="otherName"
                    />
                    <label htmlFor="shortCode">shortCode</label>
                    <Field
                        id="shortCode"
                        name="shortCode"
                        placeholder="shortCode"
                    />
                    <button type="submit">Submit</button>
                </Form>
            </Formik>
        </div>
    );
}

export default WrapperForm;
